INSERT INTO `tourism_informasi_visa` VALUES (1,'Conto Pengumuman','Exp Pengumuman',NULL,'<!DOCTYPE html>\r\n<html>\r\n<head>\r\n</head>\r\n<body>\r\n<p style=\"margin: 0.5em 0px; color: #202122; font-family: sans-serif; font-size: 14px; background-color: #ffffff;\"><strong>Charles Henry Holden</strong>&nbsp;<small style=\"font-size: 11.9px;\"><a style=\"text-decoration-line: none; color: #0b0080; background: none;\" title=\"Doctor of Letters\" href=\"https://id.wikipedia.org/wiki/Doctor_of_Letters\">Litt.D</a>,&nbsp;<a style=\"text-decoration-line: none; color: #0b0080; background: none;\" title=\"Royal Institute of British Architects\" href=\"https://id.wikipedia.org/wiki/Royal_Institute_of_British_Architects\">FRIBA</a>,&nbsp;<a class=\"new\" style=\"text-decoration-line: none; color: #a55858; background: none;\" title=\"Royal Town Planning Institute (halaman belum tersedia)\" href=\"https://id.wikipedia.org/w/index.php?title=Royal_Town_Planning_Institute&amp;action=edit&amp;redlink=1\">MRTPI</a>,&nbsp;<a class=\"new\" style=\"text-decoration-line: none; color: #a55858; background: none;\" title=\"Royal Designers for Industry (halaman belum tersedia)\" href=\"https://id.wikipedia.org/w/index.php?title=Royal_Designers_for_Industry&amp;action=edit&amp;redlink=1\">RDI</a></small>&nbsp;(12 Mei 1875 &ndash; 1 Mei 1960) adalah arsitek kelahiran&nbsp;<a style=\"text-decoration-line: none; color: #0b0080; background: none;\" title=\"Bolton\" href=\"https://id.wikipedia.org/wiki/Bolton\">Bolton</a>,&nbsp;<a style=\"text-decoration-line: none; color: #0b0080; background: none;\" title=\"Inggris\" href=\"https://id.wikipedia.org/wiki/Inggris\">Inggris</a>&nbsp;yang dikenal karena merancang banyak stasiun&nbsp;<a style=\"text-decoration-line: none; color: #0b0080; background: none;\" title=\"London Underground\" href=\"https://id.wikipedia.org/wiki/London_Underground\">London Underground</a>&nbsp;selama tahun 1920-an dan 1930-an,&nbsp;<a class=\"new\" style=\"text-decoration-line: none; color: #a55858; background: none;\" title=\"Perpustakaan Pusat Bristol (halaman belum tersedia)\" href=\"https://id.wikipedia.org/w/index.php?title=Perpustakaan_Pusat_Bristol&amp;action=edit&amp;redlink=1\">Perpustakaan Pusat Bristol</a>, kantor pusat&nbsp;<a class=\"new\" style=\"text-decoration-line: none; color: #a55858; background: none;\" title=\"Perusahaan Kereta Api Bawah Tanah Listrik London (halaman belum tersedia)\" href=\"https://id.wikipedia.org/w/index.php?title=Perusahaan_Kereta_Api_Bawah_Tanah_Listrik_London&amp;action=edit&amp;redlink=1\">Perusahaan Kereta Api Bawah Tanah Listrik London</a>&nbsp;di&nbsp;<a class=\"new\" style=\"text-decoration-line: none; color: #a55858; background: none;\" title=\"Broadway 55 (halaman belum tersedia)\" href=\"https://id.wikipedia.org/w/index.php?title=Broadway_55&amp;action=edit&amp;redlink=1\">Broadway 55</a>&nbsp;dan&nbsp;<a class=\"new\" style=\"text-decoration-line: none; color: #a55858; background: none;\" title=\"Senate House (halaman belum tersedia)\" href=\"https://id.wikipedia.org/w/index.php?title=Senate_House&amp;action=edit&amp;redlink=1\">Senate House</a>&nbsp;di&nbsp;<a style=\"text-decoration-line: none; color: #0b0080; background: none;\" title=\"Universitas London\" href=\"https://id.wikipedia.org/wiki/Universitas_London\">Universitas London</a>. Dia juga banyak mendesain kuburan perang di&nbsp;<a style=\"text-decoration-line: none; color: #0b0080; background: none;\" title=\"Belgia\" href=\"https://id.wikipedia.org/wiki/Belgia\">Belgia</a>&nbsp;dan&nbsp;<a style=\"text-decoration-line: none; color: #0b0080; background: none;\" title=\"Prancis\" href=\"https://id.wikipedia.org/wiki/Prancis\">Prancis</a>&nbsp;utara untuk&nbsp;<a class=\"new\" style=\"text-decoration-line: none; color: #a55858; background: none;\" title=\"Komisi Kuburan Perang Persemakmuran (halaman belum tersedia)\" href=\"https://id.wikipedia.org/w/index.php?title=Komisi_Kuburan_Perang_Persemakmuran&amp;action=edit&amp;r